hudson.tasks.Shell.command:
set +e TAG=$(git describe --tags --exact-match) if [ $? -eq 0 ]; then HAS_TAG=true else HAS_TAG=false fi HASH=$(git rev-parse --short HEAD) BRANCH_NAME=${GIT_BRANCH##origin/}
set -e
if [ $DOCKER_PUSH = true ]; then echo "Pushing" if [[ ! -z ${DOCKER_USERNAME+x} ]] && [[ ! -z ${DOCKER_PASSWORD+x} ]]; then if [[ -z ${DOCKER_EMAIL+x} ]]; then DOCKER_EMAIL="${DOCKER_USERNAME}@localhost" fi sudo docker login -e $DOCKER_EMAIL -u $DOCKER_USERNAME -p $DOCKER_PASSWORD $DOCKER_REGISTRY fi sudo docker push ${DOCKER_REGISTRY}/bccvl/postgis:9.5.0-$HASH sudo docker push ${DOCKER_REGISTRY}/bccvl/postgis:"9.5.0-${BRANCH_NAME}-latest" if [ $HAS_TAG = true ]; then sudo docker push ${DOCKER_REGISTRY}/bccvl/postgis:9.5.0-$TAG if [ $BRANCH_NAME = "master" ]; then sudo docker push ${DOCKER_REGISTRY}/bccvl/postgis:latest fi fi else echo "Pushing of images disabled" fi
